  			removeLink: this.make("a", {className: "remove", href:'#'}, "Remove"),
  			text: this.make("span", {className: 'tasktext' })
  		};
  		
		$(this.el).
			append(this.dom.text).
			append(this.dom.completeLink).
			append(this.dom.incompleteLink).
			append(this.dom.removeLink);
			
		this.handleEvents();
